import React, { useEffect, useState } from "react";
import { slice } from 'loadash'
import axios from "axios";
import "./App.css";

function App() {
  const [isloaded, setIsLoaded] = useState(false);
  const [cars, setCars] = useState({});
  const [i, setI] = useState(5);


  // fetches cars from api
  const fetchCars = () => {
    axios
      .get("https://mocki.io/v1/eb5a9094-f0eb-4f83-a289-fd8b0e7806e7")
      .then((response) => {
        setCars(response.data);
        console.log(cars);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const loadMore = () => {
    setI(i + 5);
    if (i >= cars.length) {
      setIsLoaded(true);
    } else {
      setIsLoaded(false);
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);
