import React, { useEffect, useState } from "react";
import { slice } from 'lodash'
import axios from "axios";
import "./App.css";
import Car from "./components.js/Car";

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [cars, setCars] = useState([]); 
  const [i, setI] = useState(3);
  let carArray;


  // fetches cars from api
  const fetchCars = () => {
    axios
    .get("https://mocki.io/v1/eb5a9094-f0eb-4f83-a289-fd8b0e7806e7")
    .then((response) => {
      setCars(response.data);
      carArray = Object.values(cars) 
      console.log(carArray);    
      const slicedCars = slice(carArray[0], 0, i)
      console.log("Sliced Array", slicedCars)    

      })
      .catch((err) => {
        console.log(err);
      });
  };

  const loadMore = () => {
    setI(i + 5);
    if (i >= cars.length) {
      setIsLoaded(true);
    } else {
      setIsLoaded(false);
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);
  return (
    <div>
      {slicedCars.map((item, index) => { 
        return (
          <div>
            <Car />
            </div>
        ); 
      })}
    </div>
  );
}

export default App;
