import React, { useEffect, useState } from "react";
import { slice } from "lodash";
import axios from "axios";
import "./App.css";
import Car from "./components.js/Car";

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [cars, setCars] = useState([]);
  const [i, setI] = useState(3);
  const slicedCars = slice(cars.cars, 0, i);

  // fetches cars from api
  const fetchCars = () => {
    axios
      .get("https://mocki.io/v1/eb5a9094-f0eb-4f83-a289-fd8b0e7806e7")
      .then((response) => {
        setCars(response.data);
        console.log(cars);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const loadMore = () => {
    setI(i + 5);
    if (i >= cars.length) {
      setIsLoaded(true);
    } else {
      setIsLoaded(false);
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);
  return (
    <div className="min-h-screen bg-emerald-800 w-full mx-[10%] py-[30vh]">

      <div className="w-full">
      {slicedCars.map((item, index) => {
        return (
          <div className="w-full">
            <Car information={item} key={index} />
          </div>
        );
      })}
      </div>

      <div className="">
        {isLoaded ? (
          <button className="px-4 py-2 rounded-md bg-blue-500 text-white my-[10%] mx-[50%] w-[10%] hover:p-6 hover:duration-500" 
            onClick={()=>{loadMore()}}>
            Done
          </button>
        ) : (
          <button  className="px-4 py-2 rounded-md bg-blue-500 text-white my-[10%] mx-[50%] w-[10%] hover:p-6 hover:duration-500" onClick={()=>{loadMore()}}>
            Load More +
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
