import React, { useEffect, useState } from "react";
import { slice } from 'lodash'
import axios from "axios";
import "./App.css";
import Car from "./components.js/Car";

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [cars, setCars] = useState({});
  const [i, setI] = useState(5);
  const slicedCars = slice(cars, 0, i)


  // fetches cars from api
  const fetchCars = () => {
    axios
      .get("https://mocki.io/v1/eb5a9094-f0eb-4f83-a289-fd8b0e7806e7")
      .then((response) => {
        setCars(response.data);
        console.log(cars);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const loadMore = () => {
    setI(i + 5);
    if (i >= cars.length) {
      setIsLoaded(true);
    } else {
      setIsLoaded(false);
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);
  return (
    <div>
      {slicedCars.map((item, index) => {
        return (
            <Car />
        ); 
      })}
      <div className="d-grid mt-3 mb-5">
        {isLoaded ? (
          <button
            onClick={loadMore}
            type="button"
            className="btn btn-danger disabled"
          >
            That's It
          </button>
        ) : (
          <button onClick={loadMore} type="button" className="btn btn-danger">
            Load More +
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
